import { Router } from "express";
import adminRouter from "./subrouters/admin.router.js";
import apiRouter from "./subrouters/api.router.js";
import publicRouter, { pageNotFound } from "./subrouters/public.router.js";
import setFlash from "../middleware/flash.middleware.js";
const router = Router();
router.use(setFlash);
router.use(publicRouter);
router.use(apiRouter);
router.use(adminRouter);
router.get("*", pageNotFound);
export default router;
